.skills {
	background-color: $main-color;
	@include flex(center, center);
	flex-flow: column wrap;

	.header {
		margin: 10px auto 50px auto;
		position: relative;

		&::after {
			@include pseudo(60%, 0, 50%, #fff, 2);
		}

		&__title {
			text-align: center;
			text-transform: uppercase;
			font-size: 2.1rem;
			z-index: 3;
			position: relative;
			margin: 0;
		}
	}

	.info {
		@include flex(center, center);
		flex-flow: column wrap;

		&__grid {
			position: relative;
			width: 78%;
			margin: 20px auto;
			padding: 0;

			&__field {
				list-style-type: none;
				position: relative;
				float: left;
				width: 27.85714285714286%;
				padding: 0 0 32.16760145166612% 0;
				transform: rotate(-60deg) skewY(30deg);
				overflow: hidden;
				visibility: hidden;

				* {
					visibility: visible;
				}

				&__hex {
					@include pseudo(100%, 0, 0, #fff);
					transform: skewY(-30deg) rotate(60deg);
					overflow: hidden;
					font-size: 3em;
					cursor: pointer;
					@include flex(center, center);

					&:hover {
						opacity: 0.5;
						transition: 0.3s;
					}

					.fa-html5 {
						color: #e44d26;
					}

					.fa-css3-alt {
						color: #379ad6;
					}

					.fa-js {
						color: #f0db4f;
					}

					.fa-database {
						color: #035976;
					}

					.fa-sass {
						color: #ce679a;
					}

					.fa-vuejs {
						color: #3fb984;
					}

					.fa-npm {
						color: #d60000;
					}

					.fa-node-js {
						color: #6da75d;
					}
				}

				&--active {
					opacity: 0.5;
					transition: 0.3s;
				}

				&:nth-child(3n + 2) {
					margin: 0 1%;
				}

				&:nth-child(6n + 4),
				&:nth-child(6n + 5),
				&:nth-child(6n + 6) {
					margin-top: -6.9285714285%;
					margin-bottom: -6.9285714285%;
					transform: translateX(50%) rotate(-60deg) skewY(30deg);
				}

				&:nth-child(6n + 4) {
					margin-left: 0.5%;
				}
			}

			&:after {
				content: '';
				display: block;
				clear: both;
			}
		}

		&__desc {
			margin: 60px 30px 10px 30px;
			padding: 30px 20px;
			max-width: 90%;
			text-align: center;
			background: #fff;
			position: relative;

			&__title {
				font-size: 1.4rem;
			}

			&::after {
				@include pseudo(100%, -10px, 10px, rgba(255, 255, 255, 0.7), -1);
			}
		}
	}
}

