sources.projects.scss:
.work {
  @include flex(center, center);
  flex-flow: column wrap;
  padding-bottom: 30px;

  .header {
    margin: 30px auto;
    position: relative;

    &::after {
      @include pseudo(60%, 0, 50%, $main-color, -1);
    }

    &__title {
      text-transform: uppercase;
      font-size: 2.1rem;
      margin: 0;
    }
  }

  .projects {
    display: grid;
    grid-template-columns: 1fr;
    margin-top: 50px;

    .project {
      width: 75vw;
      max-width: 310px;
      height: 75vw;
      max-height: 310px;
      justify-self: center;
      margin: 30px;
      position: relative;

      &:first-child .project__image {
        background-image: url("../Gallery/projekt1.PNG");
      }

      &:nth-child(2) .project__image {
        background-image: url("../Gallery/projekt2.PNG");
      }

      &:nth-child(3) .project__image {
        background-image: url("../Gallery/projekt3.PNG");
      }

      &:nth-child(4) .project__image {
        background-image: url("../Gallery/projekt4.PNG");
      }

      &:nth-child(4) .project__incentive {
        color: #fff;
      }

      &:nth-child(5) .project__image {
        background-image: url("../Gallery/projekt5.PNG");
      }

      &:nth-child(6) .project__image {
        background-image: url("../Gallery/projekt6.PNG");
      }

      &::after,
      &::before {
        @include pseudo(100%, 0, 0, transparent, -5);
      }

      &:hover::after {
        top: -10px;
        left: 10px;
        transition: 0.4s;
        background: rgba(247, 202, 24, 0.7);
      }

      &:hover::before {
        top: -20px;
        left: 20px;
        transition: 0.4s;
        background: rgba(247, 202, 24, 0.5);
      }

      &:hover .project__description {
        opacity: 1;
        transition: 0.3s;
      }

      &__image {
        @include pseudo(100%, 0, 0);
        background-position: center center;
        background-size: cover;
      }

      &__incentive {
        @include pseudo(100%, auto, 0);
        height: 10%;
        bottom: 0;
        color: #000;
        font-size: 1em;
        text-align: center;
      }

      &__description {
        position: absolute;
        width: 100%;
        position: absolute;
        top: 0;
        left: 0;
        z-index: 2;
        background: $main-color;
        opacity: 0;
        min-height: 100%;
        padding: 10px 7px;

        &__title {
          text-align: center;
          margin: 20px 10px;
          font-size: 1.3em;
          border-left: 2px solid #fff;
        }

        &__technologies {
          margin-left: 10px;
          padding-left: 15px;
          border-left: 2px solid #fff;

          ul {
            padding-left: 25px;
            margin-top: 8px;
            list-style-image: url("../Gallery/list.png");
          }
        }

        &__buttons {
          width: 100%;
          @include flex(center, center);
          margin-top: 30px;

          button {
            border: 0 none;
            background: #fff;
            padding: 10px 30px;
            border-radius: 5px;
            margin: 0 10px;
            border: 2px solid #fff;
            text-transform: uppercase;
            font-weight: bold;
            font-size: 0.8em;
            cursor: pointer;

            &:hover {
              background: $main-color;
              transition: 0.3s;
            }
          }
        }
      }
    }
  }
}

@media all and (min-width: 600px) {
  .work {
    .header {
      margin: 20px;
    }

    .projects {
      .project {
        width: 350px;
        min-height: 350px;
        font-size: 1.3em;

        .project__description .project__description__buttons {
          position: absolute;
          bottom: 30px;
          padding: 0 30px 0 20px;
          @include flex(space-around, center);

          button {
            margin: 0;
          }
        }
      }
    }
  }
}

@media all and (min-width: 850px) {
  .work {
    .projects {
      grid-template-columns: 1fr 1fr;

      .project {
        width: 300px;
        min-height: 300px;
        font-size: 1em;
      }
    }
  }
}

@media all and (min-width: 1150px) {
  .work .projects {
    grid-template-columns: 1fr 1fr 1fr;
    padding-bottom: 40px;
  }
}

@media all and (min-width: 1365px) {
  .work {
    .header {
      margin: 20px;
    }
  }
}

sources.contact.scss:
.contact {
	background: $main-color;
	@include flex(center, center);
	flex-flow: column wrap;
	padding-bottom: 10px;

	.header {
		margin: 10px auto 20px auto;
		position: relative;

		&::after {
			@include pseudo(60%, 0, 50%, #fff, 1);
		}

		&__title {
			text-transform: uppercase;
			font-size: 2.1rem;
			margin: 0;
			position: relative;
			z-index: 3;
		}
	}

	.info {
		@include flex(center, center);
		flex-flow: column wrap;
		width: 100%;

		&__form {
			@include flex(center, center);
			flex-flow: column wrap;
			width: 100%;

			&__inputs {
				@include flex(center, center);
				flex-flow: column wrap;
				margin-top: 30px;
				width: 100%;
				font-family: 'Montserrat', sans-serif;

				&__label {
					width: 100%;
					@include flex(center, center);

					input,
					textarea {
						border: 0 none;
						width: 80%;
						margin: 7px;
						padding: 10px;
						text-transform: uppercase;

						&:focus {
							border-bottom: 2px solid #000;
						}
					}

					textarea {
						height: 100px;
					}

					.--error {
						border-bottom: 2px solid red;
					}
				}
			}

			&__reset {
				padding: 6px 35px;
				background: #fff;
				text-transform: uppercase;
				font-weight: bold;
				border: 2px solid #fff;
				margin: 30px;
				cursor: pointer;

				&:hover {
					background: $main-color;
					transition: 0.3s ease-out;
				}
			}
		}

		&__socials {
			width: 100%;
			padding: 20px 40px;
			@include flex(space-evenly, center);

			&__social {
				width: 54px;
				height: 54px;
				font-size: 1.7em;
				border-radius: 10px;
				@include flex(center, center);
				position: relative;
				z-index: 2;
				overflow: hidden;
				cursor: pointer;
				color: #fff;

				&::after {
					@include pseudo(200%, 123%, -150%, #fff, -2);
					height: 160%;
					transform: rotate(-57deg);
				}

				&:hover::after {
					animation: social 0.7s ease-in-out both;
				}
			}

			&__facebook {
				background-color: #4267b2;

				&:hover {
					color: #4267b2;
				}
			}

			&__twitter {
				background-color: #1da0f1;

				&:hover {
					color: #1da0f1;
				}
			}

			&__instagram {
				background-color: #b43e88;

				&:hover {
					color: #b43e88;
				}
			}

			&__google {
				background-color: #eb4336;

				&:hover {
					color: #eb4336;
				}
			}
		}
	}
	&__copyright {
		color: #fff;
	}
}

@media all and (min-width: 860px) {
	.contact {
		.info__socials {
			margin-bottom: 20px;
			margin-top: 40px;

			&__social {
				transform: scale(1.4);
			}
		}
	}

	.info__form__inputs__message {
		height: 150px;
	}
}

@media all and (min-width: 1124px) {
	.contact {
		.info {
			padding: 20px 60px;

			&__form__inputs {
				width: 60%;

				&__label {
					input,
					textarea {
						width: 70%;
						font-size: 1.1em;
						padding: 12px;
					}
				}

				&__reset {
					font-size: 1.1em;
				}
			}

			&__socials {
				width: 46%;
			}
		}
	}
}

